import{_ as a,b as s}from"./app.cc5d8726.js";const e={},n=s('<h1 id="\u524D\u7AEF\u9762\u8BD5\u9898\u6C47\u603B-\u6D4F\u89C8\u5668\u7BC7-4" tabindex="-1"><a class="header-anchor" href="#\u524D\u7AEF\u9762\u8BD5\u9898\u6C47\u603B-\u6D4F\u89C8\u5668\u7BC7-4">\xB6</a> <span class="prefix"></span><span class="content" id="\u524D\u7AEF\u9762\u8BD5\u9898\u6C47\u603B-\u6D4F\u89C8\u5668\u7BC7-4"><a class="header-anchor" href="#\u524D\u7AEF\u9762\u8BD5\u9898\u6C47\u603B-\u6D4F\u89C8\u5668\u7BC7-4" aria-hidden="true">#</a> \u524D\u7AEF\u9762\u8BD5\u9898\u6C47\u603B-\u6D4F\u89C8\u5668\u7BC7-4</span><span class="suffix"></span></h1><h3 id="\u524D\u7AEF\u4E0E\u6570\u636E\u57CB\u70B9" tabindex="-1"><a class="header-anchor" href="#\u524D\u7AEF\u4E0E\u6570\u636E\u57CB\u70B9">\xB6</a> <span class="prefix"></span><span class="content" id="\u524D\u7AEF\u4E0E\u6570\u636E\u57CB\u70B9"><a class="header-anchor" href="#\u524D\u7AEF\u4E0E\u6570\u636E\u57CB\u70B9" aria-hidden="true">#</a> \u524D\u7AEF\u4E0E\u6570\u636E\u57CB\u70B9</span><span class="suffix"></span></h3><p>\u6240\u8C13\u57CB\u70B9\u5C31\u662F\u5728\u5E94\u7528\u4E2D\u7279\u5B9A\u7684\u6D41\u7A0B\u6536\u96C6\u4E00\u4E9B\u4FE1\u606F\uFF0C\u7528\u6765\u8DDF\u8E2A\u5E94\u7528\u4F7F\u7528\u7684\u72B6\u51B5\uFF0C\u540E\u7EED\u7528\u6765\u8FDB\u4E00\u6B65\u4F18\u5316\u4EA7\u54C1\u6216\u662F\u63D0\u4F9B\u8FD0\u8425\u7684\u6570\u636E\u652F\u6491\uFF0C\u5305\u62EC\u8BBF\u95EE\u6570\uFF08Visits\uFF09\uFF0C\u8BBF\u5BA2\u6570\uFF08Visitor\uFF09\uFF0C\u505C\u7559\u65F6\u957F\uFF08Time On Site\uFF09\uFF0C\u9875\u9762\u6D4F\u89C8\u6570\uFF08Page Views\uFF09\u548C\u8DF3\u51FA\u7387\uFF08Bounce Rate\uFF09\u3002\u8FD9\u6837\u7684\u4FE1\u606F\u6536\u96C6\u53EF\u4EE5\u5927\u81F4\u5206\u4E3A\u4E24\u79CD\uFF1A\u9875\u9762\u7EDF\u8BA1\uFF08track this virtual page view\uFF09\uFF0C\u7EDF\u8BA1\u64CD\u4F5C\u884C\u4E3A\uFF08track this button by an event\uFF09\u3002</p><blockquote class="multiquote-1"><p>\u6570\u636E\u57CB\u70B9\u7684\u65B9\u5F0F</p></blockquote><p>\u73B0\u5728\u57CB\u70B9\u7684\u4E3B\u6D41\u6709\u4E24\u79CD\u65B9\u5F0F\uFF1A</p><ul><li>\u7B2C\u4E00\u79CD\uFF1A\u81EA\u5DF1\u516C\u53F8\u7814\u53D1\u5728\u4EA7\u54C1\u4E2D\u6CE8\u5165\u4EE3\u7801\u7EDF\u8BA1\uFF0C\u5E76\u642D\u5EFA\u8D77\u76F8\u5E94\u7684\u540E\u53F0\u67E5\u8BE2\u3002</li><li>\u7B2C\u4E8C\u79CD\uFF1A\u7B2C\u4E09\u65B9\u7EDF\u8BA1\u5DE5\u5177\uFF0C\u5982\u53CB\u76DF\u3001\u795E\u7B56\u3001Talkingdata\u3001GrowingIO\u7B49\u3002</li></ul><p>\u5982\u679C\u662F\u4EA7\u54C1\u65E9\u671F\uFF0C\u901A\u5E38\u4F1A\u4F7F\u7528\u7B2C\u4E8C\u79CD\u65B9\u5F0F\u6765\u91C7\u96C6\u6570\u636E\uFF0C\u5E76\u76F4\u63A5\u4F7F\u7528\u7B2C\u4E09\u65B9\u5206\u6790\u5DE5\u5177\u8FDB\u884C\u57FA\u672C\u7684\u5206\u6790\u3002\u800C\u5BF9\u4E8E\u90A3\u4E9B\u5BF9\u6570\u636E\u5B89\u5168\u6BD4\u8F83\u91CD\u89C6\uFF0C\u4E1A\u52A1\u53C8\u76F8\u5BF9\u590D\u6742\u7684\u516C\u53F8\u5219\u901A\u5E38\u662F\u4F7F\u7528\u7B2C\u4E00\u79CD\u65B9\u5F0F\u91C7\u96C6\u6570\u636E\uFF0C\u5E76\u642D\u5EFA\u76F8\u5E94\u7684\u6570\u636E\u4EA7\u54C1\u5B9E\u73B0\u5176\u6570\u636E\u5E94\u7528\u6216\u662F\u5206\u6790\u7684\u8BC9\u6C42\u3002</p><blockquote class="multiquote-1"><p>\u5173\u952E\u6307\u6807</p></blockquote><p>\u6211\u4EEC\u5148\u770B\u770B\u65E0\u8BBA\u662FAPP\uFF0CH5\u8FD8\u662F\u5C0F\u7A0B\u5E8F\u90FD\u4F1A\u5173\u6CE8\u7684\u6307\u6807\uFF0C\u4E86\u89E3\u8FD9\u4E9B\u6307\u6807\u7684\u8BA1\u7B97\u65B9\u6CD5\u7684\u7EC6\u5FAE\u5DEE\u5F02\u4EE5\u53CA\u590D\u6742\u6027\uFF0C\u6362\u4E2A\u89D2\u5EA6\u6765\u601D\u8003\u57CB\u70B9\u7684\u610F\u4E49</p><ul><li><h3 id="\u8BBF\u95EE\u4E0E\u8BBF\u5BA2" tabindex="-1"><a class="header-anchor" href="#\u8BBF\u95EE\u4E0E\u8BBF\u5BA2">\xB6</a> <span class="prefix"></span><span class="content" id="\u8BBF\u95EE\u4E0E\u8BBF\u5BA2"><a class="header-anchor" href="#\u8BBF\u95EE\u4E0E\u8BBF\u5BA2" aria-hidden="true">#</a> \u8BBF\u95EE\u4E0E\u8BBF\u5BA2</span><span class="suffix"></span></h3></li></ul><p>\u8BBF\u95EE\u6B21\u6570\uFF08Visits\uFF09\u4E0E\u8BBF\u95EE\u4EBA\u6570\uFF08Vistors\uFF09\u662F\u51E0\u4E4E\u6240\u6709\u5E94\u7528\u90FD\u9700\u8981\u7EDF\u8BA1\u7684\u6307\u6807\uFF0C\u8FD9\u4E5F\u662F\u6700\u57FA\u7840\u7684\u6307\u6807\u3002</p><p>\u5BF9\u4E8E\u5E94\u7528\u7684\u7EDF\u8BA1\u6765\u8BF4\uFF0C\u7ECF\u5E38\u770B\u5230\u7684DAU\uFF0CMAU\uFF0CUV\u7B49\u6307\u6807\u90FD\u662F\u6307\u7EDF\u8BA1\u8BBF\u5BA2\uFF08Vistors\uFF09\u3002\u8BBF\u95EE\uFF08Visits\uFF09\u662F\u6307\u4F1A\u8BDD\u5C42\uFF0C\u7528\u6237\u6253\u5F00\u5E94\u7528\u82B1\u4E00\u6BB5\u65F6\u95F4\u6D4F\u89C8\u53C8\u79BB\u5F00\uFF0C\u4ECE\u6307\u6807\u5B9A\u4E49\uFF08\u8BBF\u95EE\u6B21\u6570\uFF09\u6765\u8BF4\u8FD9\u88AB\u79F0\u4E4B\u4E3A\u7EDF\u8BA1\u4F1A\u8BDD\uFF08Session\uFF09\u6570\u3002</p><p>\u4E00\u6B21\u4F1A\u8BDD\uFF08Session \u6216 Visit\uFF09\u662F\u6253\u5F00\u5E94\u7528\u7684\u7B2C\u4E00\u4E2A\u8BF7\u6C42\uFF08\u6253\u5F00\u5E94\u7528\uFF09\u548C\u6700\u540E\u4E00\u4E2A\u8BF7\u6C42\u51B3\u5B9A\u7684\u3002\u5982\u679C\u7528\u6237\u6253\u5F00\u5E94\u7528\u7136\u540E\u653E\u4E0B\u624B\u673A\u6216\u662F\u79BB\u5F00\u7535\u8111\uFF0C\u5E76\u5728\u63A5\u4E0B\u676530\u5206\u949F\u5185\u6CA1\u6709\u4EFB\u4F55\u52A8\u4F5C\uFF0C\u6B64\u6B21\u4F1A\u8BDD\u81EA\u52A8\u7ED3\u675F\uFF0C\u901A\u5E38\u4E5F\u7B97\u4F5C\u4E00\u6B21\u8BBF\u95EE\u6216\u4F1A\u8BDD\u671F\uFF0830\u5206\u949F\u662F\u65E9\u8D77\u7F51\u9875\u7248\u5E94\u7528\u7EA6\u5B9A\u4FD7\u6210\u7684\u4F1A\u8BDD\u6570\u5B9A\u4E49\uFF0C\u76EE\u524D\u7528\u6237\u505C\u7559\u5728\u5E94\u7528\u7684\u65F6\u957F\u53D8\u957F\uFF0C30\u5206\u949F\u7684\u9650\u5B9A\u4E5F\u53EF\u80FD\u968F\u4E4B\u4E0D\u540C\uFF0C\u603B\u4E4B\u662F\u80FD\u4EE3\u8868\u4E00\u6B21\u7528\u6237\u8BBF\u95EE\u7684\u65F6\u957F\uFF09\u3002</p><p>\u5728\u8BA1\u7B97\u8BBF\u95EE\u4EBA\u6570\uFF08Vistors\uFF09\u65F6\uFF0C\u57CB\u70B9\u4E0A\u62A5\u7684\u6570\u636E\u662F<strong>\u5C3D\u53EF\u80FD\u63A5\u8FD1\u771F\u5B9E</strong>\u8BBF\u5BA2\u7684\u4EBA\u6570\u3002\u5BF9\u4E8E\u6709\u9700\u8981\u7EDF\u8BA1\u72EC\u7ACB\u8BBF\u5BA2\u8FD9\u4E2A\u6307\u6807\u7684\u573A\u666F\uFF0C\u8FD9\u91CC\u8FD8\u662F\u9700\u8981\u5F3A\u8C03\u4E00\u4E0B\uFF0C\u8BBF\u95EE\u4EBA\u6570\uFF08Vistors\uFF09\u5E76\u4E0D\u662F\u771F\u5B9E\u72EC\u7ACB\u7684\u4EBA\uFF0C\u56E0\u6B64\u6536\u96C6\u6570\u636E\u65F6\u5FC5\u987B\u77E5\u9053\u8BBF\u95EE\u4EBA\u6570\u867D\u7136\u80FD\u591F\u5F88\u597D\u7684\u53CD\u6620\u4F7F\u7528\u5E94\u7528\u7684\u771F\u5B9E\u8BBF\u95EE\u8005\u7684\u6570\u91CF\uFF0C\u4F46\u4E0D\u7B49\u4E8E\u4F7F\u7528\u5E94\u7528\u7684\u771F\u5B9E\u4EBA\u6570\u3002\uFF08\u539F\u56E0\u662F\uFF0C\u91CD\u590D\u5B89\u88C5\u7684\u5E94\u7528\uFF0C\u6216\u662F\u624B\u673A\u53C2\u6570\u88AB\u4FEE\u6539\u90FD\u4F1A\u4F7F\u5F97\u72EC\u7ACB\u8BBF\u5BA2\u7684\u6307\u6807\u6536\u5230\u5F71\u54CD\u3002\u8BA1\u7B97\u8BBF\u95EE\u4EBA\u6570\u7684\u57CB\u70B9\u90FD\u662F\u4F9D\u8D56Cookie\uFF0C\u7528\u6237\u6253\u5F00\u5E94\u7528\uFF0C\u5E94\u7528\u90FD\u4F1A\u5728\u6B64\u4EBA\u7684\u7EC8\u7AEF\u521B\u5EFA\u4E00\u4E2A\u72EC\u7ACBCookie, Cookie\u4F1A\u88AB\u4FDD\u7559\uFF0C\u4F46\u8FD8\u662F\u96BE\u514D\u4F1A\u88AB\u7528\u6237\u624B\u52A8\u6E05\u7406\u6216\u662FCookie\u88AB\u7981\u7528\u5BFC\u81F4\u540C\u4E00\u7528\u6237\u4F7F\u7528\u5E94\u7528Cookie\u4E0D\u4E00\u81F4\uFF0C\u6240\u4EE5\u72EC\u7ACB\u8BBF\u5BA2\u53EA\u80FD<strong>\u9AD8\u5EA6\u63A5\u8FD1\u4E8E</strong>\u4F7F\u7528\u5E94\u7528\u7684\u771F\u5B9E\u4EBA\u6570\u3002\uFF09</p><ul><li><h3 id="\u505C\u7559\u65F6\u957F" tabindex="-1"><a class="header-anchor" href="#\u505C\u7559\u65F6\u957F">\xB6</a> <span class="prefix"></span><span class="content" id="\u505C\u7559\u65F6\u957F"><a class="header-anchor" href="#\u505C\u7559\u65F6\u957F" aria-hidden="true">#</a> \u505C\u7559\u65F6\u957F</span><span class="suffix"></span></h3></li></ul><p>\u505C\u7559\u65F6\u957F\u7528\u6765\u8861\u91CF\u7528\u6237\u5728\u5E94\u7528\u7684\u67D0\u4E00\u4E2A\u9875\u9762\u6216\u662F\u4E00\u6B21\u8BBF\u95EE\uFF08\u4F1A\u8BDD\uFF09\u6240\u505C\u7559\u7684\u65F6\u95F4\u3002</p><p>\u9875\u9762\u505C\u7559\u65F6\u957F\uFF0C\u8868\u793A\u5728\u6BCF\u4E2A\u9875\u9762\u6240\u82B1\u8D39\u7684\u65F6\u95F4\uFF1B\u4F8B\u5982\uFF1A\u9996\u9875\u5C31\u662F\u8FDB\u5165\u9996\u9875\uFF0810\uFF1A00\uFF09\u5230\u79BB\u5F00\u9996\u9875\u8FDB\u5165\u4E0B\u4E00\u4E2A\u9875\u9762(10:01)\u7684\u65F6\u957F\uFF0C\u9996\u9875\u505C\u7559\u65F6\u957F\u8BA1\u7B97\u4E3A1\u5206\u949F\u3002\u9875\u9762A\u662F2\u5206\u949F\u3002\u505C\u7559\u65F6\u957F\u7684\u6570\u636E\u5E76\u4E0D\u90FD\u662F\u4E00\u5B9A\u91C7\u96C6\u5F97\u5230\u7684\uFF0C\u6BD4\u5982<strong>\u9875\u9762B</strong>\u8FDB\u5165\u65F6\u95F4\uFF0810\uFF1A03\uFF09\uFF0C\u79BB\u5F00\u51FA\u73B0\u5F02\u5E38\u6216\u662F\u9000\u51FA\u65F6\u95F4\u6CA1\u6709\u8BB0\u5F55\uFF0C\u8FD9\u65F6\u5019\u8BA1\u7B97\u5C31\u662F0 \uFF08\u6240\u4EE5\u6307\u6807\u8BA1\u7B97\u65F6\u9700\u8981\u4E86\u89E3\u57CB\u70B9\u7684\u72B6\u51B5\uFF0C\u5254\u9664\u8FD9\u6837\u7684\u65E0\u6548\u6570\u636E\uFF09\u3002</p><p>\u5E94\u7528\u7684\u505C\u7559\u65F6\u957F\uFF0C\u8868\u793A\u4E00\u6B21\u8BBF\u95EE\uFF08\u4F1A\u8BDD\uFF09\u6240\u505C\u7559\u7684\u65F6\u95F4\uFF0C\u8BA1\u7B97\u8D77\u6765\u5C31\u662F\u6240\u6709\u9875\u9762\u7684\u8BBF\u95EE\u65F6\u957F\uFF0C\u540C\u6837\u662F\u4E0A\u4E00\u4E2A\u6D41\u7A0B\uFF0C\u5E94\u7528\u7684\u505C\u7559\u65F6\u957F\u5C31\u662F4\u5206\u949F\u3002</p><ul><li><h3 id="\u8DF3\u51FA\u7387" tabindex="-1"><a class="header-anchor" href="#\u8DF3\u51FA\u7387">\xB6</a> <span class="prefix"></span><span class="content" id="\u8DF3\u51FA\u7387"><a class="header-anchor" href="#\u8DF3\u51FA\u7387" aria-hidden="true">#</a> \u8DF3\u51FA\u7387</span><span class="suffix"></span></h3></li></ul><p>\u8DF3\u51FA\u7387\u7684\u8BA1\u7B97\u65B9\u6CD5\u73B0\u5728\u5728\u5404\u4E2A\u516C\u53F8\u8FD8\u662F\u5F88\u591A\u79CD\uFF0C\u6700\u7ECF\u5E38\u88AB\u4F7F\u7528\u7684\u662F\uFF1A\u7528\u6237\u53EA\u8BBF\u95EE\u4E86\u4E00\u4E2A\u9875\u9762\u6240\u5360\u7684\u4F1A\u8BDD\u6BD4\u4F8B\uFF08\u539F\u56E0\u662F\uFF1A\u5047\u8BBE\u8FD9\u79CD\u573A\u666F\uFF0C\u7528\u6237\u6765\u4E86\u8BBF\u95EE\u4E86\u4E00\u4E2A\u9875\u9762\u5C31\u79BB\u5F00\u4E86\uFF0C\u60F3\u60F3\u7528\u6237\u4F7F\u7528\u7684\u5FC3\u91CC\u753B\u9762\u5E94\u8BE5\u662F\uFF1A\u6253\u5F00\u5E94\u7528\uFF0C\u5FC3\u60F3\u4EC0\u4E48\u9B3C\uFF0C\u7136\u540E\u5173\u95ED\u5E94\u7528\u751A\u81F3\u5378\u8F7D\u4E86\u3002\u8FD9\u4E2A\u573A\u666F\u591A\u53EF\u6015\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u8DF3\u51FA\u7387\u6307\u6807\u88AB\u5982\u6B64\u5173\u6CE8\uFF09</p><p>\u8DF3\u51FA\u7387\u53EF\u4EE5\u5206\u89E3\u5230\u4E24\u4E2A\u5C42\u6B21\uFF1A\u4E00\u662F\u6574\u4E2A\u5E94\u7528\u7684\u8DF3\u51FA\u7387\uFF0C\u4E8C\u662F\u91CD\u70B9\u7684\u7740\u9646\u9875\u7684\u8DF3\u51FA\u7387\uFF0C\u751A\u81F3\u662F\u641C\u7D22\u5173\u952E\u8BCD\u7684\u8DF3\u51FA\u7387\u3002\u8DF3\u51FA\u7387\u7684\u6307\u6807\u53EF\u64CD\u4F5C\u6027\u975E\u5E38\u5F3A\uFF0C\u901A\u8FC7\u7EDF\u8BA1\u8DF3\u51FA\u7387\u53EF\u4EE5\u76F4\u63A5\u53D1\u73B0\u9875\u9762\u7684\u95EE\u9898\u53D1\u73B0\u5173\u952E\u8BCD\u7684\u95EE\u9898\u3002</p><ul><li><h3 id="\u9000\u51FA\u7387" tabindex="-1"><a class="header-anchor" href="#\u9000\u51FA\u7387">\xB6</a> <span class="prefix"></span><span class="content" id="\u9000\u51FA\u7387"><a class="header-anchor" href="#\u9000\u51FA\u7387" aria-hidden="true">#</a> \u9000\u51FA\u7387</span><span class="suffix"></span></h3></li></ul><p>\u9000\u51FA\u7387\u662F\u9488\u5BF9\u9875\u9762\u7684\uFF0C\u8FD9\u4E2A\u6307\u6807\u7684\u76EE\u6807\u5F88\u7B80\u5355\uFF0C\u5C31\u662F\u5728\u9488\u5BF9\u67D0\u4E2A\u9875\u9762\u6709\u591A\u5C11\u7528\u6237\u79BB\u5F00\u4E86\u5E94\u7528\uFF0C\u4E3B\u8981\u7528\u6237\u53CD\u6620\u7528\u6237\u4ECE\u5E94\u7528\u79BB\u5F00\u7684\u60C5\u51B5\u3002\u54EA\u4E9B\u9875\u9762\u9700\u8981\u88AB\u6539\u8FDB\u6700\u5FEB\u7684\u65B9\u5F0F\u88AB\u53D1\u6398\u3002\uFF08**\u6CE8\u610F\uFF1A\u9000\u51FA\u7387\u9AD8\u4E0D\u4E00\u5B9A\u662F\u574F\u4E8B\u3002**\u4F8B\u5982\uFF1A\u9884\u6D4B\u6D41\u7A0B\u7684\u6700\u7EC8\u8282\u70B9\u7684\u9000\u51FA\u7387\u5C31\u5E94\u8BE5\u662F\u9AD8\u7684\uFF09</p><ul><li><h3 id="\u8F6C\u5316\u7387" tabindex="-1"><a class="header-anchor" href="#\u8F6C\u5316\u7387">\xB6</a> <span class="prefix"></span><span class="content" id="\u8F6C\u5316\u7387"><a class="header-anchor" href="#\u8F6C\u5316\u7387" aria-hidden="true">#</a> \u8F6C\u5316\u7387</span><span class="suffix"></span></h3></li></ul><p>\u6211\u4EEC\u5728\u4EA7\u54C1\u4E0A\u6295\u5165\u8FD9\u4E48\u591A\uFF0C\u4E0D\u5C31\u662F\u4E3A\u4E86\u8861\u91CF\u4EA7\u51FA\u4E48\uFF1F\u6240\u4EE5\u5BF9\u4E8E\u7535\u5546\u7C7B\u5E94\u7528\uFF0C\u8FD8\u6709\u6BD4\u8F6C\u5316\u7387\u66F4\u503C\u5F97\u5173\u6CE8\u7684\u6307\u6807\u5417\uFF1F\u8F6C\u5316\u7387\u7684\u8BA1\u7B97\u65B9\u6CD5\u662F\u67D0\u79CD\u4EA7\u51FA\u9664\u4EE5\u72EC\u7ACB\u8BBF\u5BA2\u6216\u662F\u8BBF\u95EE\u91CF\uFF0C\u5BF9\u4E8E\u7535\u5546\u4EA7\u54C1\u6765\u8BF4\uFF0C\u5C31\u662F\u63D0\u4EA4\u8BA2\u5355\u7528\u6237\u6570\u9664\u4EE5\u72EC\u7ACB\u8BBF\u5BA2\u3002</p><p>\u8F6C\u5316\u7387\u7684\u8BA1\u7B97\u770B\u8D77\u6765\u60F3\u5230\u90A3\u7B80\u5355\uFF0C\u4F46\u5374\u662F\u57CB\u70B9\u4E2D\u6700\u8D34\u8FD1\u4E1A\u52A1\u7684\u6570\u636E\u6536\u96C6\u3002\u8FD9\u4E5F\u662F\u6700\u4F53\u73B0\u57CB\u70B9\u6280\u5DE7\u7684\u6307\u6807\uFF0C\u9700\u8981\u7ED3\u5408\u4E1A\u52A1\u7279\u70B9\u5236\u5B9A\u8BA1\u7B97\u65B9\u6CD5\u3002\u63D0\u4EA4\u8BA2\u5355\u91CF/\u8BBF\u5BA2\u6570\u662F\u6700\u57FA\u672C\u7684\u8F6C\u5316\u7387\uFF0C\u8F6C\u5316\u7387\u8FD8\u53EF\u4EE5\u5206\u5C42\u6B21\uFF0C\u6307\u5B9A\u7528\u6237\u8DEF\u5F84\u7684\uFF0C\u5982\uFF1A\u5B8C\u6210\u67D0\u6761\u8DEF\u5F84\u7684\u63D0\u4EA4\u8BA2\u5355\u6570/\u8BBF\u5BA2\u6570\u3002</p><p><strong>\u8BD5\u7740\u627E\u4E00\u6761\u8DEF\u5F84\uFF0C\u60F3\u60F3\u8F6C\u5316\u7387\u7684\u6570\u636E\u600E\u4E48\u5F97\u6765\u7684\u5427\uFF0C\u57CB\u70B9\u90FD\u6536\u96C6\u4E86\u4EC0\u4E48\u6837\u7684\u6570\u636E\u5427\uFF1F</strong></p><ul><li><h3 id="\u53C2\u4E0E\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u53C2\u4E0E\u5EA6">\xB6</a> <span class="prefix"></span><span class="content" id="\u53C2\u4E0E\u5EA6"><a class="header-anchor" href="#\u53C2\u4E0E\u5EA6" aria-hidden="true">#</a> \u53C2\u4E0E\u5EA6</span><span class="suffix"></span></h3></li></ul><p>\u53C2\u4E0E\u5EA6\u5E76\u4E0D\u662F\u4E00\u4E2A\u6307\u6807\uFF0C\u800C\u662F\u4E00\u7CFB\u5217\u7684\u6307\u6807\u7684\u7EDF\u79F0\uFF0C\u4F8B\u5982\u8BBF\u95EE\u6DF1\u5EA6\uFF0C\u8BBF\u95EE\u9891\u6B21\uFF0C\u9488\u5BF9\u7535\u5546\u7684\u4E0B\u5355\u6B21\u6570\uFF0C\u9488\u5BF9\u5185\u5BB9\u670D\u52A1\u5546\u7684\u64AD\u653E\u6B21\u6570\uFF0C\u53CA\u7528\u6237\u884C\u4E3A\u5E8F\u5217\u8FD9\u4E9B\u90FD\u53EF\u4EE5\u662F\u8861\u91CF\u53C2\u4E0E\u5EA6\u7684\u6307\u6807\u3002\u4E4B\u6240\u4EE5\u628A\u53C2\u4E0E\u5EA6\u5217\u4E3A\u4E00\u4E2A\u6307\u6807\uFF0C\u662F\u5E0C\u671B\u5927\u5BB6\u660E\u767D\u628A\u6307\u6807\u7ED3\u5408\u4E1A\u52A1\uFF0C\u4EA7\u751F\u5316\u5B66\u53CD\u5E94\uFF0C\u6D3B\u5B66\u6D3B\u7528\u53BB\u53D1\u73B0\u4E8B\u7269\u7684\u672C\u8D28\u3002</p><blockquote class="multiquote-1"><h3 id="\u57CB\u70B9\u7684\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u57CB\u70B9\u7684\u5185\u5BB9">\xB6</a> <span class="prefix"></span><span class="content" id="\u57CB\u70B9\u7684\u5185\u5BB9"><a class="header-anchor" href="#\u57CB\u70B9\u7684\u5185\u5BB9" aria-hidden="true">#</a> \u57CB\u70B9\u7684\u5185\u5BB9</span><span class="suffix"></span></h3></blockquote><p>\u770B\u5B8C\u5173\u952E\u7684\u8FD9\u4E9B\u6307\u6807\u540E\uFF0C\u5176\u5B9E\u57CB\u70B9\u5927\u81F4\u5206\u4E3A\u4E24\u90E8\u5206\uFF0C\u4E00\u90E8\u5206\u662F\u7EDF\u8BA1\u5E94\u7528\u9875\u9762\u8BBF\u95EE\u60C5\u51B5\uFF0C\u5373\u9875\u9762\u7EDF\u8BA1\uFF0C\u968F\u9875\u9762\u8BBF\u95EE\u52A8\u4F5C\u53D1\u751F\u65F6\u8FDB\u884C\u4E0A\u62A5\uFF1B\u53E6\u5916\u4E00\u90E8\u5206\u662F\u7EDF\u8BA1\u5E94\u7528\u5185\u7684\u64CD\u4F5C\u884C\u4E3A\uFF0C\u5728\u9875\u9762\u4E2D\u64CD\u4F5C\u65F6\u8FDB\u884C\u4E0A\u62A5\uFF08\u4F8B\u5982\uFF1A\u7EC4\u4EF6\u66DD\u5149\u65F6\uFF0C\u7EC4\u4EF6\u70B9\u51FB\u65F6\uFF0C\u4E0A\u6ED1\uFF0C\u4E0B\u6ED1\u65F6\uFF09\u3002</p><p>\u4E3A\u4E86\u7EDF\u8BA1\u5230\u6240\u9700\u8981\u7684\u6307\u6807\uFF0C\u5E94\u7528\u4E2D\u7684\u6240\u6709\u9875\u9762\uFF0C\u4E8B\u4EF6\u90FD\u88AB\u552F\u4E00\u6807\u8BB0\uFF0C\u7528\u6237\u7684\u4FE1\u606F\uFF0C\u8BBE\u5907\u7684\u4FE1\u606F\uFF0C\u65F6\u95F4\u53C2\u6570\u4EE5\u53CA\u7B26\u5408\u4E1A\u52A1\u9700\u8981\u7684\u53C2\u6570\u5177\u4F53\u5185\u5BB9\u88AB\u9644\u52A0\u4E0A\u62A5\uFF0C\u5C31\u662F\u57CB\u70B9\u3002</p><blockquote class="multiquote-1"><p>\u5173\u4E8E\u57CB\u70B9\u7684\u6570\u636E\u7684\u6CE8\u610F\u4E8B\u9879</p></blockquote><h3 id="\u5982\u4F55\u57CB\u70B9" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u57CB\u70B9">\xB6</a> <span class="prefix"></span><span class="content" id="\u5982\u4F55\u57CB\u70B9"><a class="header-anchor" href="#\u5982\u4F55\u57CB\u70B9" aria-hidden="true">#</a> \u5982\u4F55\u57CB\u70B9</span><span class="suffix"></span></h3><p>\u5173\u4E8E\u57CB\u70B9\u6570\u636E\u6709\u4E00\u70B9\u81F3\u5173\u91CD\u8981\uFF0C\u57CB\u70B9\u662F\u4E3A\u4E86\u66F4\u597D\u5730\u4F7F\u7528\u6570\u636E\uFF0C\u4E0D\u8981\u8BD5\u56FE\u5F97\u5230\u7CBE\u51C6\u7684\u6570\u636E\u8981\u5F97\u5230\u7684\u662F\u9AD8\u8D28\u91CF\u7684\u57CB\u70B9\u6570\u636E\uFF0C\u524D\u9762\u8BA8\u8BBA\u8DF3\u51FA\u7387\u5C31\u662F\u8FD9\u4E2A\u4F8B\u5B50\uFF0C\u5F97\u5230\u80FD\u5F97\u5230\u7684\u6570\u636E\uFF0C\u7528\u4E0D\u5B8C\u7F8E\u7684\u6570\u636E\u6765\u8FBE\u6210\u4E0B\u4E00\u6B65\u7684\u884C\u52A8\uFF0C\u8FFD\u6C42\u7684\u662F\u9AD8\u8D28\u91CF\u800C\u4E0D\u662F\u7CBE\u786E\u3002\u8FD9\u662F\u5F88\u591A\u6570\u636E\u4EA7\u54C1\u5BB9\u6613\u5165\u5751\u7684\u5730\uFF0C\u8981\u7ECF\u5E38\u63D0\u9192\u81EA\u5DF1\u3002</p><ol><li>\u907F\u514D\u8DE8\u57DF\uFF08img \u5929\u7136\u652F\u6301\u8DE8\u57DF\uFF09</li><li>\u5229\u7528\u7A7A\u767Dgif\u62161x1 px\u7684img\u662F\u4E92\u8054\u7F51\u5E7F\u544A\u6216\u7F51\u7AD9\u76D1\u6D4B\u65B9\u9762\u5E38\u7528\u7684\u624B\u6BB5\uFF0C\u7B80\u5355\u3001\u5B89\u5168\u3001\u76F8\u6BD4PNG/JPG\u4F53\u79EF\u5C0F\uFF0C1px \u900F\u660E\u56FE\uFF0C\u5BF9\u7F51\u9875\u5185\u5BB9\u7684\u5F71\u54CD\u51E0\u4E4E\u6CA1\u6709\u5F71\u54CD\uFF0C\u8FD9\u79CD\u8BF7\u6C42\u7528\u5728\u5F88\u591A\u5730\u65B9\uFF0C\u6BD4\u5982\u6D4F\u89C8\u3001\u70B9\u51FB\u3001\u70ED\u70B9\u3001\u5FC3\u8DF3\u3001ID\u9881\u53D1\u7B49\u7B49\uFF0C</li><li>\u56FE\u7247\u8BF7\u6C42\u4E0D\u5360\u7528 Ajax \u8BF7\u6C42\u9650\u989D</li><li>\u4E0D\u4F1A\u963B\u585E\u9875\u9762\u52A0\u8F7D\uFF0C\u5F71\u54CD\u7528\u6237\u7684\u4F53\u9A8C\uFF0C\u53EA\u8981new Image\u5BF9\u8C61\u5C31\u597D\u4E86\uFF0C\u4E00\u822C\u60C5\u51B5\u4E0B\u4E5F\u4E0D\u9700\u8981append\u5230DOM\u4E2D\uFF0C\u901A\u8FC7\u5B83\u7684onerror\u548Conload\u4E8B\u4EF6\u6765\u68C0\u6D4B\u53D1\u9001\u72B6\u6001\u3002 \u793A\u4F8B</li></ol>',36);function i(p,r){return n}var c=a(e,[["render",i]]);export{c as default};
